function esc(s) {
  return String(s||'').replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;');
}

function cellVal(v) {
  if (!v) return '<span class="cell-dash">â€”</span>';
  const u = v.toUpperCase();
  if (u === 'YES') return '<span class="cell-yes">âœ“</span>';
  if (u === 'NO')  return '<span class="cell-no">âœ—</span>';
  if (u === 'PENDING')   return '<span class="cell-pending">Pend.</span>';
  if (u === 'SCHEDULED') return '<span class="cell-sched">Sched.</span>';
  return esc(v);
}

function statusBadge(s) {
  if (!s) return '<span class="cell-dash">â€”</span>';
  const u = s.toUpperCase();
  const cls = u === 'LONG KEY COLLECTION' ? 'KEYCO' : u;
  const label = u === 'LONG KEY COLLECTION' ? 'Key Coll.' : (u.charAt(0) + u.slice(1).toLowerCase());
  return `<span class="badge badge-${cls}">${label}</span>`;
}

function matches(r, q) {
  if (!q) return true;
  return ['name','phone','pm','comments','type','status','stage'].some(
    k => r[k] && String(r[k]).toLowerCase().includes(q)
  );
}

function populateSelect(id, values) {
  const sel = document.getElementById(id);
  while (sel.options.length > 1) sel.remove(1);
  values.forEach(v => {
    const o = document.createElement('option');
    o.value = v; o.textContent = v;
    sel.appendChild(o);
  });
}

function stageDotsHtml(r) {
  return STEP_KEYS.map(k => {
    const v = (r[k]||'').toUpperCase();
    const bg = v === 'YES' ? 'var(--signed)' :
               (v === 'PENDING' || v === 'SCHEDULED') ? 'var(--active)' : 'var(--border)';
    return `<span style="display:inline-block;width:7px;height:7px;border-radius:50%;background:${bg};margin:0 1px"></span>`;
  }).join('');
}

