// â”€â”€ CSV DOWNLOAD â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function downloadCSV(type) {
  let rows, headers, filename;
  if (type === 'chyler') {
    headers = ['#','Date','Source','Phone','Type','Name','PM','Stage','Comments','Responsive','GC','1st Meet','2D','Quotation','2nd Meet','Rev 2D','Rev Quot','Site Visit','1st Deposit','2nd Deposit','Status'];
    rows = CHYLER.map(r => [r.num,r.date,r.source,r.phone,r.type,r.name,r.pm,r.stage,r.comments,r.responsive,r.gc,r.meet1,r.two_d,r.quot,r.meet2,r.rev2d,r.revquot,r.site,r.dep1,r.dep2,r.status]);
    filename = 'Chyler_Leads.csv';
  } else if (type === 'woodates') {
    headers = ['#','Date','Phone','Type','Name','PM','Stage','Comments','Responsive','GC','1st Meet','2D','Quotation','2nd Meet','Rev 2D','Rev Quot','Site Visit','1st Deposit','2nd Deposit','Status'];
    rows = WOODATES.map(r => [r.num,r.date,r.phone,r.type,r.name,r.pm,r.stage,r.comments,r.responsive,r.gc,r.meet1,r.two_d,r.quot,r.meet2,r.rev2d,r.revquot,r.site,r.dep1,r.dep2,r.status]);
    filename = 'Woodates_Leads.csv';
  } else {
    headers = ['Year','Name','1st Deposit','2nd Deposit'];
    rows = SIGNED.map(r => [r.year,r.name,r.dep1,r.dep2]);
    filename = 'Signed_Clients.csv';
  }
  const csv = '\uFEFF' + [headers,...rows].map(r =>
    r.map(v => { const s = String(v||'').replace(/"/g,'""'); return s.includes(',')||s.includes('\n')||s.includes('"') ? `"${s}"` : s; }).join(',')
  ).join('\n');
  const a = document.createElement('a');
  a.href = URL.createObjectURL(new Blob([csv], {type:'text/csv;charset=utf-8'}));
  a.download = filename;
  a.click();
}

// â”€â”€ BOOT â€” wire all events here, no inline handlers â”€â”€
document.addEventListener('DOMContentLoaded', function() {

  // Populate dropdowns
  const chylerPMs = [...new Set(CHYLER.map(r=>r.pm).filter(Boolean))].sort();
  populateSelect('pl-pm',    chylerPMs);
  populateSelect('prog-pm',  chylerPMs);
  const chylerTypes = [...new Set(CHYLER.map(r=>r.type).filter(Boolean))].sort();
  populateSelect('master-type', chylerTypes);
  const wdtPMs = [...new Set(WOODATES.map(r=>r.pm).filter(Boolean))].sort();
  populateSelect('wpl-pm',   wdtPMs);
  populateSelect('wprog-pm', wdtPMs);
  const years = [...new Set(SIGNED.map(r=>String(r.year)).filter(Boolean))].sort();
  populateSelect('signed-year', years);

  // Main tabs
  document.querySelectorAll('.main-tab').forEach(tab => {
    tab.addEventListener('click', () => switchMain(tab.dataset.section));
  });

  // Chyler sub-tabs
  document.querySelectorAll('#subnav-chyler .sub-tab').forEach(tab => {
    tab.addEventListener('click', () => switchChylerTab(tab.dataset.tab));
  });

  // Woodates sub-tabs
  document.querySelectorAll('#subnav-woodates .sub-tab').forEach(tab => {
    tab.addEventListener('click', () => switchWdtTab(tab.dataset.tab));
  });

  // Pill filters
  document.querySelectorAll('.pill[data-prefix="pl"]').forEach(pill => {
    pill.addEventListener('click', () => {
      plFilter = pill.dataset.filter;
      document.querySelectorAll('.pill[data-prefix="pl"]').forEach(p => p.classList.remove('active'));
      pill.classList.add('active');
      renderPipeline();
    });
  });

  // Search + selects â€” Chyler pipeline
  document.getElementById('pl-search').addEventListener('input', renderPipeline);
  document.getElementById('pl-pm').addEventListener('change', renderPipeline);

  // Search + selects â€” Chyler progression
  document.getElementById('prog-search').addEventListener('input', renderProgression);
  document.getElementById('prog-status').addEventListener('change', renderProgression);
  document.getElementById('prog-pm').addEventListener('change', renderProgression);

  // Search + selects â€” Chyler master
  document.getElementById('master-search').addEventListener('input', renderMaster);
  document.getElementById('master-type').addEventListener('change', renderMaster);

  // Search + selects â€” Woodates pipeline
  document.getElementById('wpl-search').addEventListener('input', renderWdtPipeline);
  document.getElementById('wpl-pm').addEventListener('change', renderWdtPipeline);

  // Search + selects â€” Woodates progression
  document.getElementById('wprog-search').addEventListener('input', renderWdtProgression);
  document.getElementById('wprog-pm').addEventListener('change', renderWdtProgression);

  // Search â€” Woodates master
  document.getElementById('wmaster-search').addEventListener('input', renderWdtMaster);

  // Search + year â€” Signed
  document.getElementById('signed-search').addEventListener('input', renderSigned);
  document.getElementById('signed-year').addEventListener('change', renderSigned);

  // Download buttons
  document.getElementById('btn-dl-chyler').addEventListener('click', () => downloadCSV('chyler'));
  document.getElementById('btn-dl-woodates').addEventListener('click', () => downloadCSV('woodates'));
  document.getElementById('btn-dl-signed').addEventListener('click', () => downloadCSV('signed'));

  // Initial render
  renderPipeline();
  renderProgression();
  renderMaster();
  renderWdtPipeline();
  renderWdtProgression();
  renderWdtMaster();
  renderSigned();
});
